package cz.wrent.advent

fun main() {
	println(part(test1))
	println(part(test2))
	println(part(test3))
	println(part(test4))
	println(part(test5))

	val result = part(input)
	println("6a: $result")
	val resultTwo = part(input, 14)
	println("6b: $resultTwo")
}

private fun part(input: String, windowSize: Int = 4): Int {
	return input.withIndex()
	.windowed(windowSize)
	.find { list->
		val set = list.map { it.value }.toSet()
		set.size == windowSize
	}!!.maxByOrNull { it.index }!!.index + 1
}

